CREATE TABLE IF NOT EXISTS `books` (
  `ID` varchar(8) NOT NULL,
  `书名` varchar(50) CHARACTER SET utf8 NOT NULL,
  `作者` text,
  `备注` text CHARACTER SET utf8 NOT NULL,
  `原价` float NOT NULL COMMENT '原价',
  `现价` float DEFAULT NULL COMMENT '现价',
  `出版社` text NOT NULL,
  `语种` text,
  `页数` int(11) DEFAULT NULL,
  `开本` int(11) NOT NULL DEFAULT '32',
  `ISBN` int(13) NOT NULL,
  `条形码` int(11) NOT NULL,
  `商品尺寸` text CHARACTER SET utf8 NOT NULL,
  `商品重量` int(11) NOT NULL,
  `品牌` text CHARACTER SET utf8 NOT NULL,
  `编辑推荐` text CHARACTER SET utf8 NOT NULL,
  `媒体推荐` text CHARACTER SET utf8 NOT NULL,
  `作者简介` text CHARACTER SET utf8 NOT NULL,
  `图片数量` int(11) NOT NULL DEFAULT '4',
  `库存` int(11) NOT NULL DEFAULT '10',
  PRIMARY KEY (`ID`)
) ENGINE=InnoDB DEFAULT CHARSET=utf32;

